import React from 'react';
// Importing some Bootstrap components for styling
import { Container, Card, Button, Row, Col } from 'react-bootstrap';

const Projects = () => {
  // You can list your projects here
  const myProjects = [
    {
      title: "Simple Game based on Unity + C#",
      description: "Arcade of racing on car in 3d pixel world. It was a school project.",
      imageUrl: "path_to_project_image.jpg",
      projectUrl: "#"
    },
    // Add more projects as needed
    {
      title: "Programming of robot",
      description: "Programming of robot that goes on a line. This could be anything from a website you built, an app, a game, or a research project.",
      imageUrl: "path_to_another_project_image.jpg",
      projectUrl: "#"
    },
    // Keep adding projects here...
  ];

  return (
    <Container>
      <h2>My Projects</h2>
      <Row>
        {myProjects.map((project, index) => (
          <Col sm={12} md={6} lg={4} className="mb-4" key={index}>
            {/* Each project is a card */}
            <Card>
              {/* Replace 'path_to_project_image.jpg' with your actual project image path */}
              <Card.Img variant="top" src={project.imageUrl} />
              <Card.Body>
                <Card.Title>{project.title}</Card.Title>
                <Card.Text>
                  {project.description}
                </Card.Text>
                {/* Add a link to your project if you have it live somewhere */}
                <Button variant="primary" href={project.projectUrl}>Check it out!</Button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
}

export default Projects;
